import React, { Component } from "react";

export default class BasicButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      buttonName: props.buttonName,
      buttonType: props.buttonType,
      aTagSetter: props.aTagSetter,
      idNumberForImage: props.idNumberOfCanvas
    };
    this.setCanvasDrawingRef = React.createRef();
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (nextProps.onClick && nextState.aTagSetter) {
      this.setCanvasDrawingRef.current.addEventListener(
        "click",
        nextProps.onClick
      );
      return true;
    } else {
      return false;
    }
  }

  render() {
    const { buttonName, buttonType, aTagSetter, idNumberForImage } = this.state;
    return (
      <React.Fragment>
        {aTagSetter ? (

          <a
            download={"image_" + idNumberForImage + ".png"}
            href={"plac"}
            id={this.props.buttonId + idNumberForImage}
            ref={this.setCanvasDrawingRef}
          >
            <span>{buttonName}</span>
          </a>
        ) : (
          <button type={buttonType} onClick={() => this.props.onClick()}>
            {buttonName}
          </button>
        )}
      </React.Fragment>
    );
  }
}
