import React, { Component } from "react";

export default class BasicButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      buttonName: props.buttonName,
      buttonType: props.buttonType,
      disabledState: false
    };
    this.setCanvasDrawingRef = React.createRef();
    this.disableButtonWhenSetting = this.disableButtonWhenSetting.bind(this);
  }

  disableButtonWhenSetting() {
    this.props.onClick();
    this.setState({
      disabledState: true,
    });
  }

  render() {
    const { buttonName, buttonType, disabledState } = this.state;
    return (
      <React.Fragment>
        {buttonName === 'Set drawing' ?
          <div>
            {disabledState}
            <button disabled={disabledState} type={buttonType} onClick={this.disableButtonWhenSetting}>
              {disabledState ? 'Drawing has been set' : buttonName}
            </button>
          </div>
          :
          <div>
            {disabledState}
            <button disabled={disabledState} type={buttonType} onClick={() => this.props.onClick()}>
              {buttonName}
            </button>
          </div>
        }
      </React.Fragment>
    );
  }
}
