import React, { Component } from "react";
import "./../static/App.css";
import CanvasBlock from "./CanvasBlock";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      setDrawings: []
    };
    this.appendDrawingToState = this.appendDrawingToState.bind(this);
  }

  appendDrawingToState(canvasDataURL) {
    this.setState(prevState => ({
      setDrawings: [...prevState.setDrawings, canvasDataURL]
    }));
  }

  componentDidMount() {
    this.fetchClearDatalist();
  }

  componentDidUpdate() {
    console.log('SETDRAWINGS', this.state.setDrawings);
  }

  async fetchClearDatalist() {
    const response = await fetch('/clear_datalist');
    const awaitedRes = await response.json();
    if (response.ok) {
      console.log('OK', awaitedRes);
    } else {
      console.log('ERROR', awaitedRes)
    }
  }

  render() {
    return (
      <div>
        <h1>ShaVas</h1>
        <hr />
        <div className="App">
          <CanvasBlock idNumber={0} appendDrawingToState={this.appendDrawingToState} />
          <CanvasBlock idNumber={1} appendDrawingToState={this.appendDrawingToState} />
        </div>
      </div>
    );
  }
};
