import React, { Component } from "react";

export default class BasicButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      buttonName: props.buttonName,
      buttonType: props.buttonType,
      disabledState: false
    };
    this.disableButtonWhenSetting = this.disableButtonWhenSetting.bind(this);
  }

  disableButtonWhenSetting() {
    this.props.onClick();
    this.setState({
      disabledState: true,
    });
  }

  render() {
    const { buttonName, buttonType, disabledState } = this.state;
    return (
      <React.Fragment>
        <button disabled={disabledState} type={buttonType} onClick={buttonName === 'Set drawing' ? this.disableButtonWhenSetting : this.props.onClick}>
          {buttonName === 'Set drawing' ?
            disabledState ? 'Drawing has been set' : buttonName
            :
            buttonName
          }
        </button>
      </React.Fragment>
    );
  }
}
