import React, { Component } from "react";
import "./../static/App.css";
import CanvasBlock from "./CanvasBlock";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      trainDrawings: [],
      setIdentifyerDrawing: null,
      disableGenerateButton: false,
    };
    this.appendDrawingToState = this.appendDrawingToState.bind(this);

    this.generateDataAndNeuronalNetwork = this.generateDataAndNeuronalNetwork.bind(this);
  }

  appendDrawingToState(canvasDataURL) {
    this.setState(prevState => ({
      trainDrawings: [...prevState.trainDrawings, canvasDataURL]
    }));
  }

  componentDidMount() {
    this.fetchClearDatalist();
  }

  componentDidUpdate() {
    console.log('trainDrawings', this.state.trainDrawings);
  }

  async fetchClearDatalist() {
    const response = await fetch('/clear_datalist');
    const awaitedRes = await response.json();
    if (response.ok) {
      console.log('OK', awaitedRes);
    } else {
      console.log('ERROR', awaitedRes)
    }
  }

  async generateDataAndNeuronalNetwork() {
    if (!(this.state.trainDrawings.length > 1)) {
      alert('Please set both drawings before generating data!')
    } else {
      this.setState({
        disableGenerateButton: true
      });
    }

    // const obj = { 'data': this.state.trainDrawings };
    // const response = await fetch('/send_canvas', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    //   body: JSON.stringify(obj)
    // })
    // const awaitedRes = await response.json();
    // if (response.ok && awaitedRes === 'appended') {
    //   console.log('OK', awaitedRes);
    // } else {
    //   console.log('ERROR', awaitedRes)
    // }
  }

  async identifyCanvasContent() {

  }

  setIdentifyerDrawing() {

  }

  render() {
    const { disableGenerateButton, trainDrawings } = this.state;

    return (
      <div>
        <h1>ShaVas</h1>
        <hr />
        <div className="App">
          <CanvasBlock
            appendDrawingToState={this.appendDrawingToState}
            identifyer={false}
            idNumber={0}
          />
          <CanvasBlock
            appendDrawingToState={this.appendDrawingToState}
            identifyer={false}
            idNumber={1}
          />
        </div>
        <button disabled={disableGenerateButton} type='button' onClick={this.generateDataAndNeuronalNetwork}>
          {disableGenerateButton === false ?
            trainDrawings.length === 2 ? 'Generate data!' : 'Please set drawings!'
            :
            'Generating, calculating...'
          }
        </button>
        <div id="identifyer-container">
          <CanvasBlock idNumber={2} identifyer={true}></CanvasBlock>
        </div>
      </div>
    );
  }
};
