import React, { Component } from "react";

export default class BasicButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };
    this.disableButtonWhenSetting = this.disableButtonWhenSetting.bind(this);
  }

  disableButtonWhenSetting() {
    this.props.onClick();
    this.setState({
      disabledState: true,
    });
  }

  render() {
    const { buttonName, buttonType, disabledState, onClick } = this.props;
    return (
      <React.Fragment>
        <button disabled={disabledState} type={buttonType} onClick={onClick}>
          {buttonName}
        </button>
      </React.Fragment>
    );
  }
}
